import { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import { ServicioServicios } from "App/Dominio/Datos/Servicios/ServicioServicios";
import { RepositorioServiciosDb } from "App/Infrestructura/Implementacion/BaseDatos/RepositorioServiciosDB";

const NodeCache = require("node-cache");
const cache = new NodeCache({useClones: false});

export default class ControladorModulos {
    private servicio: ServicioServicios
    constructor() {
        this.servicio = new ServicioServicios(new RepositorioServiciosDb())
  }

  /**
   * @swagger
   * /api/v1/servicios/{iddispositivo}:
   *   get:
   *     tags:
   *       - Servicios
   *     summary: Busca los servicios mas frecuentes con el identificador del celular.
   *     consumes:
   *       - application/json
   *     produces: application/json
   *     parameters:
   *       - name: dispositivo
   *         description: identificador unico del dispositivo
   *         in: path
   *         type: string
   *         required: true
   *     responses:
   *       200:
   *           description: Acción ejecutada correctamente.
   *           content:
   *               application/json:
   *                   schema:
   *                       type: array
   *                       items:
   *                           type: object
   *                           properties:
   *                                   id:
   *                                      type: number
   *                                      descripcion: identificacion del servicio
   *                                   nombre:
   *                                      type: string
   *                                      descripcion: nombre del servicio
   *                                   filename:
   *                                      type: string
   *                                      descripcion: icono del servicio
   *                                   orden:
   *                                      type: number
   *                                      descripcion: orden del servicio
   *                                   json:
   *                                      type: object
   *                                      properties:
   *                                        id_tipo_reporte:
   *                                          type: number
   *                                          descripcion: identificador del tipo de reporte
   *                                        nombre:
   *                                          type: string
   *                                          descripcion: nombre del tipo de reporte
   *                                        descripcion:
   *                                          type: string
   *                                          descripcion: descripcion tipo de reporte
   *                                        orden:
   *                                          type: number
   *                                          descripcion: orden tipo de reporte
   *                                        despues:
   *                                          type: number
   *                                          descripcion: siguiente tipo de reporte
   *                                        icono:
   *                                          type: string
   *                                          descripcion: icono del tipo de reporte
   *                                        json-plantilla:
   *                                          type: object
   *                                        url_servicio:
   *                                           type: string
   *                                           descripcion: url del servicio
   *                                        id_padre:
   *                                           type: number
   *                                           nulleable: true
   *                                           descripcion: identificador del padre
   *                                        version:
   *                                            type: number
   *                                            descripcion: version del tipo de servicio
   *                                   tipo:
   *                                      type: number
   *                                      descripcion: tipo de servicio
   *                   example:
   *                                [
   *                                   {
   *                                       "id": 1,
   *                                       "nombre": "Huecos y Baches",
   *                                      "filename": "data:image/png;base64,",
   *                                      "ruta": "huecos",
   *                                      "estado": true,
   *                                      "orden": 1,
   *                                      "json": {
   *                                          "id_tipo_reporte": 2,
   *                                          "nombre": "Huecos y Baches",
   *                                          "descripcion": "Reporta huecos y baches en la vía pública.",
   *                                          "orden": "2",
   *                                          "despues": 1,
   *                                          "icono": "data:image/png;base64,",
   *                                          "json_plantilla": {
   *                                              "TITULO": "huecos",
   *                                              "plantilla": "3",
   *                                              "title_alert": "",
   *                                              "alert": "",
   *                                              "title_ok": "Su reporte se ha realizado",
   *                                              "ok": "Gracias, en este momento enviaremos su reporte al área encargada para gestionar la solución.",
   *                                              "secciones": {
   *                                                  "mapa": {
   *                                                      "titulo": "Ubicación del reporte",
   *                                                      "labeldireccion": "Dirección del daño en la vía",
   *                                                      "placeholderdir": "",
   *                                                      "ayudadir": "Puede digitar la dirección donde se encuentra el daño o ubicar el PIN en el mapa",
   *                                                      "requeridodir": "1",
   *                                                      "labelref": "Digite un punto de referencia de la dirección",
   *                                                      "placeholderref": "Ejemplo: Cerca al consumo de la 80...",
   *                                                      "ayudaref": "El punto de referencia permitirá ubicar fácilmente la ubicación del daño.",
   *                                                      "requeridoref": "0",
   *                                                      "labelheaderform": "Datos del reporte",
   *                                                      "labelformImages": "Fotografía (s) de evidencia"
   *                                                  },
   *                                                  "fotos": {
   *                                                      "cantidad": 1,
   *                                                      "requeridas": 1
   *                                                  },
   *                                                  "reporte": {
   *                                                      "titulo": "Datos del reporte",
   *                                                      "selects": [],
   *                                                      "cajas": [
   *                                                          {
   *                                                              "label": "Ingresa el correo electrónico de quién reporta",
   *                                                              "placeholder": "",
   *                                                              "ayuda": "",
   *                                                              "requerido": 1,
   *                                                              "alias": "correo",
   *                                                              "tipocampo": "C"
   *                                                          }
   *                                                      ]
   *                                                  }
   *                                              }
   *                                          },
   *                                          "url_servicio": "https://www.medellin.gov.co/api-reportesmed/reporte",
   *                                          "id_padre": null,
   *                                          "version": 1
   *                                      },
   *                                  }
   *                              ]
   */

    public async buscar({ params }: HttpContextContract) {
        return this.servicio.buscar(params.dispositivo)

    }

    /**
   * @swagger
   * /api/v1/servicios/interaccion:
   *   post:
   *     tags:
   *       - Servicios
   *     summary: Se envía en formato json el id del servicio  y el identificador del teléfono.
   *     produces: application/json
   *     parameters:
   *       - name: interaccion
   *         description: interaccion
   *         in: body
   *         required: true
   *         schema:
   *           type: object
   *           properties:
   *             servicio_id:
   *               type: number
   *               descripcion: identificador del servicio
   *             dispositivo:
   *               type: string
   *               descripcion: identificador del telefono
   *     responses:
   *       200:
   *         description: peticion exitosa
   *         content:
   *               application/json:
   *                   schema:
   *                       type: boolean
   *                       descripcion: respuesta del servidor
   *                   example:
   *                           true
   */

    public async interaccion({ request }: HttpContextContract) {
        const datos = request.all();
        const servicio = datos.servicio_id;
        const dispositivo = datos.dispositivo
        return this.servicio.guardarInteraccion(servicio, dispositivo)

    }

}
