        const libro = new Libro();
        libro.titulo = titulo;
        libro.isbn = isbn;
        libro.id_editorial = editorialId;
        libro.id_autor = autorId;
        await libro.save();
        return {libro, "msj": "Libro registrado con xito"};
    }

    public async getListarLibros(): Promise<Libro[]>{
        const libros = await Libro.all();
        return libros;
    }
}

